function fix(foo){
	foo=(foo+0).toFixed(1);
	if(foo[foo.length-1]==0){
		foo=foo.split("");
		foo.pop();
		foo.pop();
		foo=foo.join("");
		return foo;
	}else{
		foo=foo-0;
		return foo.toFixed(1);
	}
}
