function Chat(entry, fg) {
    if (GLB.showCombatLog === "Off") {
        return;
    }
    while (NG.combatLog.childNodes.length > 100) {
        NG.combatLog.removeChild(NG.combatLog.firstChild);
    }
    var color = "";
    if (fg !== undefined) {
		if (fg === 0) {
            color = "white";
        } else if (fg === 1) {
            color = "red";
        } else if (fg === 2) {
            color = "yellow";
        } else if (fg === 3) {
            color = "blue1";
        } else if (fg === 4) {
            color = "blue2";
        } else if (fg === 5) {
            color = "darkgreen";
        } else if (fg === 6) {
            color = "green3";
        }  else if (fg === 7) {
            color = "purple";
        } else if (fg === 8){
			color = "yellow2";
		} else {
            color = "grey";
        }
    }
    var z = D.createElement('div');
    if (color!==undefined) {
        z.className = color;
    }
    z.innerHTML = entry;
    NG.combatLog.appendChild(z);
	if(chatVerticalDrag===false){
		NG.combatLog.scrollTop = NG.combatLog.scrollHeight;
	}
}
