function hide(e) {
    if (typeof e === "string") {
        D.getElementById(e).style.visibility = 'hidden';
    } else {
        var len = e.length;
        for (var i = 0; i < len; i++) {
            D.getElementById(e[i]).style.visibility = 'hidden';
        }
    }
}

function show(e) {
    if (typeof e === "string") {
        D.getElementById(e).style.visibility = 'visible';
    } else {
        var len = e.length;
        for (var i = 0; i < len; i++) {
            D.getElementById(e[i]).style.visibility = 'visible';
        }
    }
}

function none(e) {
    if (typeof e === "string") {
        D.getElementById(e).style.display = 'none';
    } else {
        var len = e.length;
        for (var i = 0; i < len; i++) {
            D.getElementById(e[i]).style.display = 'none';
        }
    }
}

function block(e) {
    if (typeof e === "string") {
        D.getElementById(e).style.display = 'block';
    } else {
        var len = e.length;
        for (var i = 0; i < len; i++) {
            var z = D.getElementById(e[i]);
			if (z !== null){
				z.style.display = 'block';
			}
        }
    }
}

function byIdCheck(e) {
    if (document.getElementById(e) != null) {
        return D.getElementById(e);
    } else {
        return false;
    }
}

