function createChar() {
    playAudio('button_2');
    resetCharButtons();
    my.difficulty = 1;
    my.lastName = "";
    my.level = 1;
    my.exp = 0;
    my.oneHandSlash = 0;
    my.twoHandSlash = 0;
    my.oneHandBlunt = 0;
    my.twoHandBlunt = 0;
    my.piercing = 0;
    my.handtohand = 1;
    my.defense = 1;
    my.offense = 1;
    my.dualWield = 0;
    my.doubleAttack = 0;
    my.dodge = 0;
    my.riposte = 0;
    my.parry = 0;
    my.alteration = 0;
    my.evocation = 0;
    my.conjuration = 0;
    my.abjuration = 0;
    my.channeling = 0;
    my.svpoison = 10;
    my.svmagic = 10;
    my.svlightning = 10;
    my.svcold = 10;
    my.svfire = 10;
    my.str += Cstr + Gstr;
    my.sta += Csta + Gsta;
    my.wis += Cwis + Gwis;
    my.intel += Cint;
    my.dex += Cdex;
    my.agi += Cagi;
    my.playtime = 0;
    my.deaths = 0;
    my.mobsSlain = 0;
    my.escapes = 0;
    my.totalGold = 0;
    my.uniquesFound = 0;
    my.raresFound = 0;
    my.magicFound = 0;
    my.upgrades = 0;
    my.raresSlain = 0;
    my.championsSlain = 0;
    my.epicQuests = 0;
    my.quests = 0;
    my.comboOverall = 0;
    my.comboPermafrost = 0;
    my.comboKedgeKeep = 0;
    my.comboSolB = 0;
    my.comboMistmoore = 0;
    my.comboLowerGuk = 0;
    my.comboCazicThule = 0;
    my.comboPlaneofFear = 0;
    my.comboPlaneofHate = 0;
    for (var i = 1; i <= 12; i++) {
        my['talent' + i] = 0;
    }
    // initialize my weapon skills
    if (my.job === "Warrior" || my.job === "Ranger" || my.job === "Paladin" || my.job === "Shadow Knight") {
        my.oneHandSlash = 1;
        my.twoHandSlash = 1;
        my.oneHandBlunt = 1;
        my.twoHandBlunt = 1;
    }
    if (my.job === "Rogue" || my.job === "Warrior" || my.job === "Ranger") {
        my.oneHandSlash = 1;
        my.piercing = 1;
        my.oneHandBlunt = 1;
    }
    if (my.job === "Bard") {
        my.oneHandSlash = 1;
        my.piercing = 1;
        my.oneHandBlunt = 1;
    }
    if (my.job === "Cleric" || my.job === "Shaman" || my.job === "Monk") {
        my.oneHandBlunt = 1;
        my.twoHandBlunt = 1;
    }
    if (my.job === "Monk") {
        my.dualWield = 1;
    }
    if (my.job === "Necromancer" || my.job === "Enchanter" || my.job === "Magician" || my.job === "Wizard") {
        my.piercing = 1;
        my.twoHandBlunt = 1;
        my.oneHandBlunt = 1;
    }
    if (my.job === "Druid") {
        my.oneHandBlunt = 1;
        my.twoHandBlunt = 1;
        my.oneHandSlash = 1;
    }
    // initialize racial casting values
    if (my.job === "Bard" || my.job === "Ranger" || my.job === "Necromancer" || my.job === "Enchanter" || my.job === "Magician" || my.job === "Druid" || my.job === "Shadow Knight" || my.job === "Shaman" || my.job === "Wizard" || my.job === "Paladin" || my.job === "Cleric") {
        my.alteration = 1;
        my.evocation = 1;
        my.conjuration = 1;
        my.abjuration = 1;
        my.channeling = 1;
    }
    // set racial resist bonuses set initial zone
    my.zone = "Edenburg";
    if (my.race === "Human") {
        my.svpoison = my.svpoison + 5;
        my.svlightning = my.svlightning + 5;
    }
    if (my.race === "Barbarian") {
        my.svcold = my.svcold + 10;
    }
    if (my.race === "Dwarf") {
        my.svpoison = my.svpoison + 5;
        my.svmagic = my.svmagic + 5;
    }
    if (my.race === "Erudite") {
        my.svfire = my.svfire + 5;
        my.svmagic = my.svmagic + 5;
        my.svlightning = my.svlightning - 5;
    }
    if (my.race === "Halfling") {
        my.svpoison = my.svpoison + 5;
        my.svlightning = my.svlightning - 5;
    }
    if (my.race === "Gnome") {
        my.svlightning = my.svlightning + 10;
    }
    if (my.race === "Troll") {
        my.svfire = my.svfire - 10;
    }
    if (my.race === "Ogre") {
        my.svmagic = my.svmagic - 5;
    }
    if (my.race === "High Elf" || my.race === "Wood Elf" || my.race === "Dark Elf" || my.race === "Half Elf") {
        my.svfire = my.svfire + 5;
        my.svcold = my.svcold + 5;
    }
    var x = my.zone;
    my.zoneN = x;
    my.zoneH = x;
    my.subzone = 0;
    my.subzoneN = 0;
    my.subzoneH = 0;
    levelUpStats();
    // max mp 100 if non-caster
    if (my.job === "Warrior" || my.job === "Monk" || my.job === "Rogue") {
        my.maxMp = 100;
        my.mp = 0;
    }
    //buttons
    for (var i = 0; i <= 23; i++) {
        Lmy['C' + i] = "";
    }
    if ($("#normalId").hasClass('ccActive') === true) {
        my.hardcoreMode = "false";
    } else {
        my.hardcoreMode = "true";
    }
    getUniqueID(); //char creation

}

function getUniqueID() {
    function do1() {
		loadServerCharacters();
        T.to('#ccBg', .5, {
            opacity: 0,
            onComplete: function() {
                (this.target).css('display', 'none');
                $("#characterSelectScreen").css({
                    display: 'block',
                    opacity: 0
                });
                T.to("#characterSelectScreen", .5, {
                    opacity: 1
                });
            }
        });
    }
    QMsg("Creating Character...");
    D.getElementById('ccBg').style.display = 'none';
	$.ajax({
		data: {
			run: "createCharacter",
			my: my
		}
	}).done(function(data) {
		if (data !== '') { //error
			D.getElementById('ccBg').style.display = 'block';
			QMsg(data);
		} else {
			do1();
		}
	}).fail(function() {
		Error('Unable to communicate with the server.<br>Please try again later.');
	});
}
