function animateRampage(){
	flashScreen("#f00",.3,3);
	function Pgo(interval){
		var X1 = M.random()*(640)-320;
		var Y1 = M.random()*(200)+149;
		if(X1<0){
			var X2 = -960;
		}else{
			var X2 = 960;
		}
		if(Y1<249){
			var Y2 = -200;
		}else{
			var Y2 = 700;
		}
		var p1 = can('redparticle50', 5, X1, Y1, 1280, 200);
		p1.image.onload = function(){
			p1.alpha=.1;
			T.to(p1, 1.5, {
				y:Y2,
				x:X2,
				onComplete:function(){
					cRem(5, p1);
				}
			});
		}
		if(interval<20){ 
			T.delayedCall(interval/1000, function(){
				Pgo(++interval);
			});
		}
	}
	Pgo(10);
}
