function damagePet(damage) {
    damRed = 0;
    damRed += (g.conjurationEquip + g.allSkillsEquip) / 3.5;
    if (damRed > 50) {
        damRed = 50;
    }
    damage = damage * (1 - (damRed / 100));
    return M.ceil(damage);
}

