function setCurtainColor(color) {
    spellCurtain.graphics
        .clear()
        .beginFill(color)
        .drawRect(0, 0, 1280, 720)
        .endFill();
}

function flashScreen(color, o, d, reverse) {
    if (!reverse) {
        setCurtainColor(color);
        T.to(spellCurtain, d, {
            startAt: {
                alpha: o
            },
            alpha: 0,
            ease: ez.Qin
        });
    } else {
        setCurtainColor(color);
        T.to(spellCurtain, d, {
            startAt: {
                alpha: 0
            },
            alpha: o,
            ease: ez.Qin,
            onComplete: function() {
                if (my.hp > 0) {
                    spellCurtain.alpha = 0;
                }
            }
        });
    }
}

