function getNewSlotNumber() {
    var count = countMobs();
    if (count >= 5) {
        newSlotNumber = false;
    } else if (count === 0) {
        newSlotNumber = 2;
    } else {
        function doit() {
            var x = ~~(M.random() * (5));
            if (!mob[x].name) {
                newSlotNumber = x;
            } else {
                doit();
            }
        }
        doit();
    }
}

function DADJ(Mlvl) {
    if (g.difficulty === 2) {
        if (Mlvl < 75) {
            var adj = ~~((75 - Mlvl) / 2);
            Mlvl += adj;
        }
    } else if (g.difficulty === 3) {
        if (Mlvl < 94) {
            var adj = ~~((94 - Mlvl) / 2);
            Mlvl += adj;
        }
    }
    return Mlvl;
}

