function getItemTier(Slot) {
	var minBump = M.random() * (.33) + M.random() * (.33) + M.random() * (.33);
	var d = diff();
	var x = mob[Slot].level;
	if (d === 0) {
		if (x > 55) {
			x = 55;
		}
	} else if (d === 1) {
		if (x > 80) {
			x = 80;
		}
	} else if (d === 2) {
		if (x > 99) {
			x = 99;
		}
	}
	var min = x * minBump;
	if (min < 1) {
		min = 1;
	}
	var range = mob[Slot].level - min;
	var foo = M.ceil(M.random() * range + min);
	if (foo > 99) {
		foo = 99;
	}
	if (foo < 1) {
		foo = 1;
	}
	return foo;
}

