function DA(x){ // dAdj
	var adj=0;
	if(g.difficulty===2){ 
		x+=25; 
		if(x<75){
			adj = ~~((75-x)/2);
		}
		x+=adj;
	}
	if(g.difficulty===3){ 
		x+=40;
		if(x<94){
			adj = ~~((94-x)/2);
		}
		x+=adj;
	}
	return x;
}
